import {useState, useEffect} from 'react';
import axios from 'axios';
import Article from '../components/Article'

function Status(){

    const [articleList, setArticleList] = useState([]);

    useEffect( () =>{
        async function getData(){
        var response = await axios.get("http://localhost:4000/articles",
         {headers: {Accept: "application/json"}})
         setArticleList(response.data)
        }
        getData();
    }, [])

    function pubStatus(val){
        if(val == true){
            return(
                <div>Published</div>
            )
        }
        else{
            return(
                <div>Not Published</div>
            )
        }
    }

    return (
        <div>
            <div className="statusNav">
                <button>Published Articles</button>
                <button>Non-Published Articles</button>

            </div>

            <div>
            {
                articleList.map(function(i, index){
                    return( 
                     <li key={index}>
                        {i.title} {pubStatus(i.published)}
                     </li>
                    )
                   })
            }
            </div>
        </div>
    )

}

export default Status;
