import {useEffect, useState} from 'react'
import axios from 'axios';
import Article from '../components/Article'

function Search(){
    const [articles, setArticles] = useState([])
    const [query, setQuery] = useState('')


    function handleSearchQuery(e){
        e.preventDefault()
        setQuery(e.target.value)

    }

    async function searchArticles(e){
        e.preventDefault()
        var response = await axios.get("http://localhost:4000/articles",
        {headers: {Accept: "application/json"}})
        setArticles(response.data)
    }
    return(
        <div>
            <br></br>
            <input value={query} onChange={handleSearchQuery} />
            <button onClick={searchArticles}>Search</button>
            {


                articles.map(function(i, index){
                    console.log("mixer")
                    if((String(i.title).toLowerCase()).includes(query)){
                    return( 
                     <li key={index}>

                         <Article title={i.title} published={i.published}/>
                     </li>
                    )}
                   })
            }


        </div>
    )
}
export default Search;
