import {useState, useEffect} from 'react';
import axios from 'axios';
import Article from './Article'

function Edit(props){
    const [edit, setEdit] = useState(true)
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [published, setPublished] = useState('');

    function handleEdit(){
        setEdit(false)
    }

    function handleTitle(e){
        e.preventDefault();
        setTitle(e.target.value)

    }
    function handleBody(e){
        e.preventDefault();
        setBody(e.target.value)

    }
    function handlePublished(e){
        e.preventDefault();
        if (e.target.value == null){
            setPublished(false)
        }
        else{
            setPublished(true)
        }

    }

    async function handleUpdate(e){

        e.preventDefault();
        try{
        var response = await axios.puts("http://localhost:4000/articles",
        {title: title, body: body, published: published},
        {headers: {accept: 'application/json'}}
        )
        var data = response.data

        alert("Article has been Submitted")
        setTitle('')
        setBody('')
        setPublished(false)

        } catch(e){
            alert("An error occured")

        }
    }

    if(edit== true){
        return(
            <div>
                <button onClick={handleEdit}>Edit</button>
                </div>

        )


    }else{
        return(
            <div>

                <div>
                <lable>
                    Title:
                    <input value={props.title} type="text" onChange={handleTitle}/>
                </lable>
                <br></br>
                <br></br>
                <label>
                    Body:
                    <textarea value={props.body} type="text" onChange={handleBody}/>
                </label>
                <br></br>
                <br></br>
                <label>
                    Published:
                    <input value={props.published} type="checkbox" onChange={handlePublished} />
                </label>
                <br></br>
                <button onClick={handleUpdate}> Update</button>
            </div>
            </div>
        )

    }


}

export default Edit;
