function Article(props){

    function showContents(id){
        document.getElementById(id+"Content").style.display="block";
                document.getElementById("moreButton"+id).style.visibility="hidden";
                document.getElementById("lessButton"+id).style.visibility="visible";

    }

    function hideContents(id){
        document.getElementById(id+"Content").style.display="none";
        document.getElementById("moreButton"+id).style.visibility="visible";
        document.getElementById("lessButton"+id).style.visibility="hidden";

    }


    return (
        <div>
            <h3>
                {props.published ? "Published: " +props.title : "Not Published: "+ props.title}
            </h3>
            <div>
                <button className={'moreButton'+props.id} style={{visibility: "visible"}} onClick={showContents(props.id)}> View Article and Details </button>

                <div className={props.id+'Content'} style={{display: "none"}}>
                    <h4>First Uploaded:  {props.created.substr(0, 10)}  </h4>
                    <h4>Article Contents:</h4>
                    <p>{props.body}</p>

                </div>
                <button className={'lessButton'+props.id} style={{visibility: "hidden"}} onClick={hideContents(props.id)} > Show Less </button>
            </div>
        </div>
    )
}
export default Article;
