import Edit from '../components/Edit'
import Article from '../components/Article'
import {Link, useLocation} from 'react-router-dom'
import { useEffect, useState } from "react";


function EditPage(props){
    const location = useLocation()

    const [locationState, setLocationState] = useState({articleTitle: '', articleBody: '', articlePublished: '', articleId: ''})

    useEffect(() =>{
        console.log("info passed", location)
        if(location.state){
            setLocationState(location.state)
        }
    },[])

    return (

        <div>

            <Edit title={props.title} published={props.published} id={props.id} body={props.body} />
        </div>

    )



}

export default EditPage;
