import {useState, useEffect} from 'react';
import axios from 'axios';
import Article from '../components/Article'

function Status(){

    const [articleList, setArticleList] = useState([]);
    const [pub, setPub] = useState(null)

    useEffect( () =>{
        async function getData(){
        var response = await axios.get("http://localhost:4000/articles",
         {headers: {Accept: "application/json"}})
         setArticleList(response.data)
        }
        getData();
    }, [])

    function publishedHandle(e){
        e.preventDefault();
        setPub(true)


    }

    function nonPublishedHandle(e){
        e.preventDefault();
        setPub(false)
    }


    return (
        <div>
            <div className="statusNav">
                <button onClick={publishedHandle}>Published Articles</button>
                <button onClick={nonPublishedHandle}>Non-Published Articles</button>
                {call}
                {
                    (function call() {
                        if (pub == true) {
                            <div>
                            {   
                                articleList.map(function(i, index){
                                    if(i.published == true){
                                        return( 
                                            <li key={index}>
                                                published
                                                {i.title}{i.published}
                                            </li>
                                            )

                                    }

                                    })

                            }

                            </div>
                        } else if (pub == false) {
                            <div>
                            {   
                                articleList.map(function(i, index){
                                    if(i.published !== true){
                                        return( 
                                            <li key={index}>
                                                published
                                                {i.title}{i.published}
                                            </li>
                                            )

                                    }

                                    })

                            }

                            </div>
                        } else {
                          return (
                            <div>nothing</div>
                          )
                        }
                      })
                }

            </div>



        </div>
    )



}

export default Status;
